  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'dex',
	sidebar_label: 'DEX 开发指南',
	title: 'Tutorial：使用 Muta 框架从零开发一条 Dex 专有链'
};
export const metadata = {
  "id": "docs_zh/dex",
  "title": "Tutorial：使用 Muta 框架从零开发一条 Dex 专有链",
  "description": "我们的目标是开发一条链上挂单、链上撮合、链上成交的简易 dex 专有链，旨在通过 step by step 的流程，帮助开发者熟悉 Muta 框架，学会如何使用框架开发自己的区块链。",
  "source": "@site/docs/docs_zh/dex.md",
  "permalink": "/muta-docs2/docs/docs_zh/dex",
